import React, { useState } from 'react';
import Produto from './Produto';

function Produtos() {
    const [categoria, setCategoria] = useState('todos');

    const produtos = [
        { nome: 'iPhone 16 Pro Max Titânio', categoria: 'iphone', preco: 8999.99, imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/182538541/image-0.jpg?v=638724823034400000' },
        { nome: 'iPhone 16 Pro Max Rose', categoria: 'iphone', preco: 8999.99, imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/182538253/image-0.jpg?v=638724822505400000' },
        { nome: 'iPhone 16 Pro Max Branco', categoria: 'iphone', preco: 8999.99, imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/182538321/image-0.jpg?v=638724822625830000' },
        { nome: 'Apple Macbook Pro', categoria: 'macbook', preco: 13499.99, imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/183133039/image-0.jpg?v=638738658814070000' },
        { nome: 'Apple Macbook Air Processador M1', categoria: 'macbook', preco: 13499.99, imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/182538508/image-0.jpg?v=638724822978230000' },
        { nome: 'Apple Macbook Air 15 Pol M3', categoria: 'macbook', preco: 13499.99, imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/182691291/image-0.jpg?v=638727423670330000' },
        { nome: 'Ipad Air De 11 Polegadas', categoria: 'ipad', preco: 2999.99, imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/181539284/image-0.jpg?v=638696916463370000' },
        { nome: 'Ipad Pro De 11 Polegadas', categoria: 'ipad', preco: 2999.99, imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/177177398/ipad-pro-de-11-polegadas-wi-fi-2tb-com-vidro-convencional-preto-espacial.jpg?v=638659985156330000' },
        { nome: 'Ipad Pro De 13 Polegadas', categoria: 'ipad', preco: 2999.99, imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/178691873/ipad-pro-de-13-polegadas-wi-fi-1tb-com-vidro-convencional-prateado.jpg?v=638668506298570000' }
    ];

    const produtosFiltrados = categoria === 'todos' ? produtos : produtos.filter(p => p.categoria === categoria);

    return (
        <div className="container">
            <h1 className="text-center my-4">Nossos Produtos</h1>
            
            <div className="text-center mb-4">
                <button className={`btn mx-2 ${categoria === 'todos' ? 'btn-primary' : 'btn-secondary'}`} onClick={() => setCategoria('todos')}>Todos</button>
                <button className="btn btn-secondary mx-2" onClick={() => setCategoria('iphone')}>iPhones</button>
                <button className="btn btn-secondary mx-2" onClick={() => setCategoria('macbook')}>MacBooks</button>
                <button className="btn btn-secondary mx-2" onClick={() => setCategoria('ipad')}>iPads</button>
            </div>
            //className={`btn mx-2 ${categoria === 'todos' ? 'btn-primary' : 'btn-secondary'}`} onClick={() => setCategoria('todos')}
            <div className="row">
                {produtosFiltrados.map((produto, index) => (
                    <div className="col-md-4 mb-4" key={index}>
                        <Produto nome={produto.nome} preco={produto.preco} imagem={produto.imagem} />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Produtos;
