import React from 'react';
import Produto from './Produto';

function Produtos() {
    const produtos = [
        {
            nome: 'iPhone 16 Pro Max',
            descricao: 'Concebido para a inteligência da Apple, com um fantástico design em titânio.',
            preco: 1999.99,
            imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/182538541/image-0.jpg?v=638724823034400000'
        },
        {
            nome: 'Apple Macbook Pro',
            descricao: 'Apple Macbook Pro A2251 Intel Core I5 10gen Ssd 500gb 16gb Macos.',
            preco: 3499.99,
            imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/183133039/image-0.jpg?v=638738658814070000'
        },
        {
            nome: '',
            descricao: 'Fone de ouvido sem fio com ótima qualidade de som, aprova de água e tudo mais',
            preco: 2999.99,
            imagem: 'https://carrefourbr.vtexassets.com/arquivos/ids/171867951/3e73e7b685264939ac83bcea8c93a726.jpg?v=638612574110100000'
        },
        {
            nome: 'Fone de Ouvido Bluetooth',
            descricao: 'Fone de ouvido sem fio com ótima qualidade de som, aprova de água e tudo mais',
            preco: 2999.99,
            imagem: 'https://via.placeholder.com/300'
        }
    ];

    return (
        <div className="container">
            <h1 className="text-center my-4">Nossos Produtos</h1>
            <div className="row">
                {produtos.map((produto, index) => (
                    <div className="col-md-4 mb-4" key={index}>
                        <Produto
                            nome={produto.nome}
                            descricao={produto.descricao}
                            preco={produto.preco}
                            imagem={produto.imagem}
                        />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Produtos;
